const app = express();

// AI-powered API endpoint
app.get('/ai-completion', async (req, res) => {
  const { prompt } = req.query;

  // Simulate AI processing
  const completion = `AI response to: ${prompt}`;

  res.json({ completion });
});

app.listen(3000, () => {
  console.log('Server running on port 3000');
});"
